import React, { useEffect, useState } from "react";

import PartyList from "../components/PartyList";
import ErrorModal from "../../shared/UIElemnets/ErrorModal";
import LoadingSpinner from "../../shared/UIElemnets/LoadingSpinner";
import "./Parties.css";

const Parties = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(undefined);
    const [loadedParties, setLoadedParties] = useState();

    useEffect(()=> {
        // const sendRequest = async () => {
        //     setIsLoading(true);
        //     try {
        //         const response = await fetch('http://localhost:4000/parties');
        //         const resData = await response.json();
        //         if (!response.ok) {
        //             throw new Error(resData.message);
        //         }
        //         setLoadedParties(resData.allParties);
        //     } catch (err) {
        //         setError(err);
        //     }
        //     setIsLoading(false);
        // };
        let sendRequest = ()=> {
            setIsLoading(true);
            fetch('http://localhost:4000/parties')
                .then(res=>res.json())
                .then(par=> setLoadedParties(par.allParties))
                .then(responce=> { if (!responce.ok) {
                        throw new Error(responce.message)
                    } })
                .catch(err=> setError(err));
            setIsLoading(false);
        };
        sendRequest()
    }, []);

    const errorHandler = () => {
        setError(null);
    };

    return (
    <React.Fragment> 
        <div className="parties">
            <div className="parties-header">
                <h1>Parties</h1>
            </div>
            <ErrorModal error={error} onClick={errorHandler} />
            {isLoading && (
                <div className="center">
                    <LoadingSpinner />
                </div>
            )}
            {!isLoading && loadedParties && <PartyList items={loadedParties} isLoggedIn={false}/>}
        </div>
    </React.Fragment>);
}

export default Parties;
