import React, { useContext, useState } from "react";

import Card from "../../shared/UIElemnets/Card";
import Button from "../../shared/UIElemnets/Button";
import Modal from "../../shared/UIElemnets/Modal";
import Input from "../../shared/UIElemnets/Input";
import { useForm } from "../../shared/hooks/form-hook";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import { AuthContext } from "../../shared/context/auth-context";
import "./PartyItem.css";

const PartyItem = props => {
    const [showModal, setShowModal] = useState(false);
    const openModalHandler = () => setShowModal(true);
    const closeModalHandler = () => setShowModal(false);
    const auth = useContext(AuthContext);

    const [isUpdatingMode, setUpadingMode] = useState(false);
    const toUpdatingMode = () => setUpadingMode(true);

    const [formState, inputHandler, setFromData] = useForm({
        title: {
            value: '',
            isValid: false
        },
        description: {
            value: '',
            isValid: false
        }
    },
        false
    );

    const submitHandler = event => {
        // setFormData(
        //     {
        //       title: {
        //         value: responseData.place.title,
        //         isValid: true
        //       },
        //       description: {
        //         value: responseData.place.description,
        //         isValid: true
        //       }
        //     },
        //     true
        //   );

        setUpadingMode(false);
        setShowModal(false);
    };

    const deleteParty = () => {};

    return (
        <React.Fragment>
            <Modal
                show={showModal}
                onClose={closeModalHandler}
                onSubmit={submitHandler}
                header={
                    <React.Fragment>
                        {isUpdatingMode ? 
                        (<Input
                            id="title" 
                            label="Name your party:" 
                            element="input" 
                            validators={[VALIDATOR_REQUIRE()]} 
                            errorText="Plaese enter a valid title"
                            onInput={inputHandler}
                            value={props.title}
                        />) : 
                        (<h2>{props.title}</h2>)}
                        <Button onClick={closeModalHandler}>X</Button>
                    </React.Fragment>
                }
                footer={
                    <React.Fragment>
                        {(props.creator === auth.userId) ? (<Button onClick={deleteParty}>DELETE</Button>) : null}
                        {(props.creator === auth.userId)
                            ? 
                            (isUpdatingMode ? (<Button danger onClick={submitHandler}>SAVE CHANGES</Button>) : (<Button onClick={toUpdatingMode}>UPDATE</Button>))
                            : 
                            ((<Button>ATTEND</Button>))}
                    </React.Fragment>
                }
            >
                <img
                    src={props.image}
                    alt={props.title}
                />
                <h3>About:</h3>
                {isUpdatingMode ? 
                (<Input
                    id="description" 
                    label="What's goin on there(description):" 
                    element="input" 
                    validators={[VALIDATOR_MINLENGTH(5)]} 
                    errorText="Plaese enter a valid description(min. 5 symbols)"
                    onInput={inputHandler}
                    value={props.description}
                />) : 
                (<h2>{props.description}</h2>)}
                <h3>We're partying at:</h3>
                <p>{props.address}</p>
            </Modal>
            <li className="party-item">
                <Card>
                    <div className="party-item-image">
                        <img
                            src={props.image}
                            alt={props.title}
                        />
                    </div>
                    <div className="party-item-footer">
                        <h2>{props.title}</h2>
                        <div className="party-item-buttons">
                            <Button onClick={openModalHandler}>DETAILS</Button>
                            {props.isLoggedIn ? null : ((props.creator === auth.userId) ? null : (<Button>ATTEND</Button>))}
                        </div>
                    </div>
                </Card>
            </li>
        </React.Fragment>
    );
}

export default PartyItem;
