import React from "react";

import Button from "../../shared/UIElemnets/Button";
import Card from "../../shared/UIElemnets/Card";
import Input from "../../shared/UIElemnets/Input";
import { useForm } from "../../shared/hooks/form-hook";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import "./NewParty.css"

const NewParty = props => {

    const [formState, inputHandler] = useForm({
        title: {
            value: '',
            isValid: false
        },
        description: {
            value: '',
            isValid: false
        },
        address: {
            value: '',
            isValid: false
        }
        },
        false
    );

    return (
        <React.Fragment>
            <Card className="new-party">
                <React.Fragment>
                    <form>
                        <Input 
                            id="title" 
                            label="Name your party:" 
                            element="input" 
                            validators={[VALIDATOR_REQUIRE()]} 
                            errorText="Plaese enter a valid party-title"/>
                        <Input 
                            id="description" 
                            label="What's goin on there(description):" 
                            element="input" 
                            validators={[VALIDATOR_MINLENGTH(15)]} 
                            errorText="Plaese enter a valid party-description(min. 15 symbols)"/>
                        <h2>IMAGE UPLOAD</h2>
                        <Input 
                            id="address" 
                            label="Address:" 
                            element="input" 
                            validators={[VALIDATOR_REQUIRE()]} 
                            errorText="Plaese enter a valid party-address"/>
                        <Button>CREATE PARTY</Button>
                    </form>
                </React.Fragment>
            </Card>
        </React.Fragment>

    )
}

export default NewParty;
