import React, { useEffect, useState } from "react";

import PartyList from "../components/PartyList";
import ErrorModal from "../../shared/UIElemnets/ErrorModal";
import LoadingSpinner from "../../shared/UIElemnets/LoadingSpinner";
import "./Parties.css";

const DUMMY_DATA = [
    {
        id: "p1",
        title: "TLV hook up",
        description: "fantastic party in tel-aviv",
        image: "https://media-cdn.tripadvisor.com/media/photo-s/14/03/b3/4e/tlv.jpg",
        address: "Carlebach St 4, Tel Aviv-Yafo, 6713208",
        creatorId: "someId1"
    },
    {
        id: "p2",
        title: "TLV hook up",
        description: "fantastic party in tel-aviv",
        image: "https://media-cdn.tripadvisor.com/media/photo-s/14/03/b3/4e/tlv.jpg",
        address: "Carlebach St 4, Tel Aviv-Yafo, 6713208",
        creatorId: "someId3"
    },
    {
        id: "p3",
        title: "TLV hook up",
        description: "fantastic party in tel-aviv",
        image: "https://media-cdn.tripadvisor.com/media/photo-s/14/03/b3/4e/tlv.jpg",
        address: "Carlebach St 4, Tel Aviv-Yafo, 6713208",
        creatorId: "someId3"
    }
]


const Parties = () => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(undefined);
    const [loadedParties, setLoadedParties] = useState();

    useEffect(()=> {
        const sendRequest = async () => {
            setIsLoading(true);
            try {
                let response = await fetch('http://localhost:4000/parties');
                let resData = await response.json();

                if (!response.ok) {
                    throw new Error(resData.message);
                }
                setLoadedParties(resData.allParties);
            } catch (err) {
            setError(err.message);
            };
        };
        setIsLoading(false);
        sendRequest();
    }, []);

    const errorHandler = () => {
        setError(null);
    };

    return (
    <React.Fragment> 
        <div className="parties">
            <div className="parties-header">
                <h1>Parties</h1>
            </div>
            <PartyList items={DUMMY_DATA} isLoggedIn={false}/>
        </div>
    </React.Fragment>);
}

export default Parties;
