import React, { useState } from "react";

import Button from "../shared/UIElemnets/Button";
import { VALIDATOR_REQUIRE } from "../shared/util/validators";
import Card from "../shared/UIElemnets/Card";
import Input from "../shared/UIElemnets/Input";
import "./Auth.css";

const Auth = props =>{
    const [isLoginMode, setIsLoginMode] = useState(true)

    const switchModeHandler = () => {
        setIsLoginMode(prevMode => !prevMode);
    }

    return (
        <React.Fragment>
            {isLoginMode ? (
            <Card className="auth">
                <h2>LOGIN</h2>
                <Input 
                    id="username" 
                    label="username" 
                    element="input" 
                    errorText="Plaese enter a valid username"/>
                <Input 
                    id="password" 
                    label="password" 
                    element="input" 
                    type="password" 
                    errorText="Plaese enter a valid password" />
                <Button inverse onClick={switchModeHandler}>
                    SWITCH TO {isLoginMode ? 'SIGNUP' : 'LOGIN'}
                </Button>
                <Button>LOGIN</Button>
            </Card>
            ) : (
            <Card className="auth">
                <h2>SIGNUP</h2>
                <Button inverse onClick={switchModeHandler}>
                    SWITCH TO {isLoginMode ? 'SIGNUP' : 'LOGIN'}
                </Button>
            </Card>
            )
            }

        </React.Fragment>
    )
}

export default Auth;
